        function validNumericInput(numberIn) {
            const error = document.getElementById("error");
            for (let i = 0; i < numberIn.length; i++) {
                if (!/^[0-9]+$/.test(numberIn[i])) {
                    error.innerHTML = "The form requires a number for this input.";
                    return false;
                }
            }
            return parseInt(numberIn);
        }
        function updateCard(cardID, data) {
            const sectionId = `card${cardID}`;
            const title = document.getElementById(sectionId).getElementsByTagName('h4')[0];
            const content = document.getElementById(sectionId).getElementsByTagName('div')[0];
           
            title.innerText = data.parse.title;
            // content.innerHTML = data.parse.text.toString();
            content.innerHTML = data.parse.text['*'];

        }
function clearCard() {
