function objectToString (object) {
  let stringObject =  Object.entries(object).map((mapResponse) => {
        let key = mapResponse[0];
        let value = mapResponse[1];
        return `${key}="${value}"`;
    }).join(',\n        ');
    return "\n        "+stringObject;
}

